if($this->config->get('payment_two_status')) {
  if ((utf8_strlen(trim($this->request->post['firstname'])) < 1)) {
    $this->error['firstname'] = $this->language->get('error_firstname_e');
  }

  if ((utf8_strlen(trim($this->request->post['lastname'])) < 1)) {
    $this->error['lastname'] = $this->language->get('error_lastname_e');
  }

  if ((utf8_strlen($this->request->post['email']) < 1)) {
    $this->error['email'] = $this->language->get('error_email_e');
  }

  if ((utf8_strlen($this->request->post['telephone']) < 1)) {
    $this->error['telephone'] = $this->language->get('error_telephone_e');
  }

  if ((utf8_strlen($this->request->post['telephone']) < 8) || (utf8_strlen($this->request->post['telephone']) > 15)) {
    $this->error['telephone'] = $this->language->get('error_telephone');
  }

  if ((utf8_strlen(trim($this->request->post['address_1'])) < 1)) {
    $this->error['address_1'] = $this->language->get('error_address_e');
  }

  if ((utf8_strlen(trim($this->request->post['city'])) < 1)) {
    $this->error['city'] = $this->language->get('error_city_e');
  }

  if ((utf8_strlen(trim($this->request->post['postcode'])) < 4) || (utf8_strlen(trim($this->request->post['postcode'])) > 9)) {
    $this->error['postcode'] = $this->language->get('error_postcode');
  }

  if ((utf8_strlen(trim($this->request->post['postcode'])) < 1)) {
    $this->error['postcode'] = $this->language->get('error_postcode_e');
  }

  if($this->customer->getAccountType() == 'business'){
    if ($this->request->post['company_id'] == '' && in_array($this->request->post['country_id'], array(222,160))) {
      $this->error['company'] = $this->language->get('error_organization_number_e');
    }
  }
}


